  <div id="page-content-wrapper">
    <div class="content-container">
      <br />
      <div class="container-fluid" style="width: auto">
        <h4>Available Products</h4>
        <div class="row">
          <div class="col-md-3" *ngFor="let product of categoryProducts">
            <div class="thumbnail">
              <img
                [src]="product.imageUrl"
                alt="Product Image"
              />
              <div class="caption">
                <h3>{{ product.name }}</h3>
                <p>Ingreidents: {{ product.description }}</p>
                <p>Total Quantity: {{ product.quantity }}</p>
                <p>Price: {{ product.price }}$</p>
                <div class="clearfix row">
                  <div class="col-md-6">
                    <input
                      type="number"
                      class="form-control"
                      placeholder="Quantity"
                      [(ngModel)]="product.quantityCounter"
                      (ngModelChange)="resetQuantity(product)"
                      [disabled]="product.quantity === 0"
                    />
                  </div>
                  <div class="col-md-6 d-flex justify-content-end">
                    <button
                      class="btn btn-warning btn-block"
                      type="button"
                      (click)="addToCart(product, product.name)"
                      [disabled]="product.quantity === 0"
                    >
                      Add To Cart
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      <br />
    </div>
