import { Component, OnInit, ViewChild } from '@angular/core';
import { HomepageService } from './homepage.service';
import { ActivatedRoute, Router } from '@angular/router';
import { LoginComponent } from '../login/login.component';
import { LoginServices } from '../login/login.service';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { SideBarComponent } from '../header-customer/side-bar/side-bar.component';

@Component({
  selector: 'app-homepage',
  templateUrl: './homepage.component.html',
  styleUrls: ['./homepage.component.css'],
})
export class HomepageComponent implements OnInit {
  customers: any[] = [];
  Message = '';
  form1!: FormGroup;
  CheckEmpty = 'No Elements Found';

  ElementProdut: any[] = [];
  ElementCustomer: any[] = [];

  constructor(
    private homepageService: HomepageService,
    private route: ActivatedRoute,
    private router: Router,
    private loginService: LoginServices,
    private fb: FormBuilder
  ) {
    this.form1 = this.fb.group({
      name: [null, [Validators.required], []],
    });
  }

  ngOnInit(): void {
    this.getAllUsers();
    this.loginService.getMessage().subscribe((message) => {
      this.Message = message;
    });
  }
  Search() {
    this.ElementCustomer = [];
    this.ElementProdut = [];
    this.CheckEmpty = '';
    this.homepageService.getbySearch([this.form1.value.name]).subscribe({
      next: (res) => {
        res.forEach((item) => {
          if (item.customer) {
            this.ElementCustomer.push(item.customer);
          }
          if (item.product) {
            this.ElementProdut.push(item.product);
          }
        });
      },
      error: (error) => {
        if (error.status === 404) this.CheckEmpty = 'No Elements Found';
      },
    });
  }
  onInputChange(value: string) {
    this.Search();
  }
  getAllUsers(): void {
    this.homepageService.getAllUsers().subscribe({
      next: (data: any[]) => {
        this.customers = data;
      },
      error: (error) => {
        console.error('Error Fetching customers:', error);
      },
    });
  }
}
<app-header></app-header>
<div class="container-fluid">
  <div class="row">
    <div class="col-md-12">
      <h3 class="text text-center">{{ Message }}</h3>
      <br />
      <br />
      <form [formGroup]="form1" class="navbar-form text-center">
        <div class="input-group col-md-5">
          <label for="search">Search Bar</label>
          <input
            type="text"
            class="form-control"
            formControlName="name"
            placeholder="Write something to get it !"
            (ngModelChange)="onInputChange($event)"
          />
        </div>
      </form>
      <br />
      <br />
      <table class="table table-striped" *ngIf="ElementCustomer.length > 0">
        <thead>
          <tr>
            <th scope="col">Name</th>
            <th scope="col">Email</th>
            <th scope="col">Country</th>
            <th scope="col">City</th>
            <th scope="col">Address</th>
            <th scope="col">Phone</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let customer of ElementCustomer">
            <td>{{ customer.name }}</td>
            <td>{{ customer.email }}</td>
            <td>{{ customer.country }}</td>
            <td>{{ customer.city }}</td>
            <td>{{ customer.address }}</td>
            <td>{{ customer.phone }}</td>
          </tr>
        </tbody>
      </table>
      <table class="table table-striped" *ngIf="ElementProdut.length > 0">
        <thead>
          <tr>
            <th scope="col">Product Name</th>
            <th scope="col">Product Quantity</th>
            <th scope="col">Product Price</th>
            <th scope="col">Product Description</th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let product of ElementProdut">
            <td>{{ product.name }}</td>
            <td>{{ product.quantity }}</td>
            <td>{{ product.price }}</td>
            <td>{{ product.description }}</td>
          </tr>
        </tbody>
      </table>
      <h5 class="text text-center">{{ CheckEmpty }}</h5>
    </div>
  </div>
</div>
