import { Component, ElementRef, OnInit, ViewChild } from '@angular/core';
import { HomepageService } from '../homepage.service';
import { ActivatedRoute, Router } from '@angular/router';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { SignupService } from 'src/app/sign-up/sign-up.service';
import { count } from 'rxjs';

@Component({
  selector: 'app-add-user',
  templateUrl: './add-user.component.html',
  styleUrls: ['./add-user.component.css'],
})
export class AddUserComponent implements OnInit {
  @ViewChild('nameInput', { static: true })
  nameInput!: ElementRef<HTMLInputElement>;
  @ViewChild('emailInput', { static: true })
  emailInput!: ElementRef<HTMLInputElement>;
  @ViewChild('passwordInput', { static: true })
  passwordInput!: ElementRef<HTMLInputElement>;
  @ViewChild('dateOfBirthInput', { static: true })
  dateOfBirthInput!: ElementRef<HTMLInputElement>;
  @ViewChild('countryInput', { static: true })
  countryInput!: ElementRef<HTMLInputElement>;
  @ViewChild('cityInput', { static: true })
  cityInput!: ElementRef<HTMLInputElement>;
  @ViewChild('addressInput', { static: true })
  addressInput!: ElementRef<HTMLInputElement>;
  @ViewChild('phoneNumberInput', { static: true })
  phoneNumberInput!: ElementRef<HTMLInputElement>;
  customers: any[] = [];
  isAccountCreated: boolean = false;
  alert = true;
  form!: FormGroup;
  selectedFile: File | null = null;

  showModal: boolean = false;
  Message = '';
  constructor(
    private fb: FormBuilder,
    private service: SignupService,
    private router: Router,
    private currentRoute: ActivatedRoute,
    private hoempageService: HomepageService
  ) {
    this.form = this.fb.group({
      name: [null, [Validators.required], []],
      email: [null, [Validators.required], []],
      password: [null, [Validators.required], []],
      city: [null, [Validators.required], []],
      country: [null, [Validators.required], []],
      address: [null, [Validators.required], []],
      dateofbirth: [null, [Validators.required], []],
      phone: [null, [Validators.required], []],
    });
  }
  ngOnInit(): void {
    this.getAllUsers();
  }

  openModal() {
    this.showModal = true;
  }

  closeModal() {
    this.showModal = false;
  }
  Check() {
    const name = this.nameInput?.nativeElement?.value;
    const email = this.emailInput?.nativeElement?.value;
    const password = this.passwordInput?.nativeElement?.value;
    const city = this.cityInput?.nativeElement?.value;
    const address = this.addressInput?.nativeElement?.value;
    const country = this.countryInput?.nativeElement?.value;
    const dateofbirth = this.dateOfBirthInput?.nativeElement?.value;
    const phone = this.phoneNumberInput?.nativeElement?.value;

    if (
      name !== '' &&
      email !== '' &&
      password !== '' &&
      city !== '' &&
      address !== '' &&
      country !== '' &&
      dateofbirth !== '' &&
      phone !== ''
    ) {
      this.alert = true;
      return true;
    }
    this.Message = 'Please Fill All The Fields';
    return false;
  }

  onFileChange(event: Event) {
    const inputElement = event.target as HTMLInputElement;
    if (inputElement.files && inputElement.files.length > 0) {
      this.selectedFile = inputElement.files[0];
    }
  }
  resetData(){
    this.Message = ''
    this.isAccountCreated = false
    this.nameInput.nativeElement.value = ''
    this.addressInput.nativeElement.value = ''
    this.cityInput.nativeElement.value = ''
    this.countryInput.nativeElement.value = ''
    this.dateOfBirthInput.nativeElement.value = ''
    this.passwordInput.nativeElement.value = ''
    this.phoneNumberInput.nativeElement.value = ''
    this.emailInput.nativeElement.value = ''
  }
  public submit() {
    const formData = new FormData();
    formData.append('name', this.form.value.name);
    formData.append('email', this.form.value.email);
    formData.append('password', this.form.value.password);
    formData.append('city', this.form.value.city);
    formData.append('country', this.form.value.country);
    formData.append('address', this.form.value.address);
    formData.append('dateofbirth', this.form.value.dateofbirth);
    formData.append('phone', this.form.value.phone);

    var base64String!: string;
    if (this.selectedFile) {
      const reader = new FileReader();
      reader.onload = (event) => {
        base64String = reader.result as string;
      };
    }
    this.service
      .addCustomer(formData)

      .subscribe({
        next: (res) => {
          if (res.includes('Created')) {
            this.Message = 'New user created Successfully!';
            this.isAccountCreated = true;
            this.customers.push({
              name: this.form.value.name,
              email: this.form.value.email,
              password: this.form.value.password,
              city: this.form.value.city,
              country: this.form.value.country,
              address: this.form.value.address,
              dateofbirth: this.form.value.dateofbirth,
              phone: this.form.value.phone,
            });
          }
        },
        error: (error) => {
          if (
            error.status === 500 &&
            error.error.includes('This name already exists')
          ) {
            this.Message = 'Username Already Exists';
            this.isAccountCreated = false;
          } else if (
            error.status === 500 &&
            error.error.includes('This email already exists')
          ) {
            this.Message = 'Email already exists';
            this.isAccountCreated = false;
          } else if (
            error.status === 500 &&
            error.error.includes('This Phone already exists')
          ) {
            this.Message = 'Phone already exists';
            this.isAccountCreated = false;
          }
        },
      });
  }
  getAllUsers(): void {
    this.hoempageService.getAllUsers().subscribe({
      next: (data: any[]) => {
        this.customers = data;
      },
      error: (error) => {
        console.error('Error fetching customers:', error);
      },
    });
  }
}
<app-header></app-header>

<div class="container-fluid">
  <div class="row">
    <div class="col-md-12">
      <br />
      <button
        class="btn btn-primary"
        data-bs-toggle="modal"
        data-bs-target="#addUserModal"
        (click)="resetData()"
      >
        Add New User
      </button>
      <div
        class="modal"
        id="addUserModal"
        tabindex="-1"
        role="dialog"
        aria-labelledby="addUserModalLabel"
        aria-hidden="true"
      >
        <div class="modal-dialog">
          <div class="modal-content">
            <div class="modal-header">
              <button
                type="button"
                class="close p-2 mt-1"
                data-bs-dismiss="modal"
                aria-label="Close"
              >
                <span aria-hidden="true">&times;</span>
              </button>
              <h4 class="modal-title" id="addUserModalLabel">Add New User</h4>
            </div>
            <div class="modal-body">
              <form [formGroup]="form" (ngSubmit)="submit()">
                <div class="form-group">
                  <label for="name">Name</label>
                  <input
                    type="text"
                    formControlName="name"
                    id="name"
                    class="form-control"
                    placeholder="Please enter your Name"
                    #nameInput
                  />
                </div>
                <div class="form-group">
                  <label for="email">Email</label>
                  <input
                    type="text"
                    formControlName="email"
                    id="email"
                    class="form-control"
                    placeholder="Please enter your Email"
                    #emailInput
                  />
                </div>
                <div class="form-group">
                  <label for="password">Password</label>
                  <input
                    type="text"
                    formControlName="password"
                    id="password"
                    class="form-control"
                    placeholder="Please enter your Password"
                    #passwordInput
                  />
                </div>
                <div class="form-group">
                  <label for="country">Country</label>
                  <input
                    type="text"
                    formControlName="country"
                    id="Country"
                    class="form-control"
                    placeholder="Enter your Country"
                    #countryInput
                  />
                </div>
                <div class="form-group">
                  <label for="city">City</label>
                  <input
                    type="text"
                    formControlName="city"
                    id="City"
                    class="form-control"
                    placeholder="Enter your City"
                    #cityInput
                  />
                </div>
                <div class="form-group">
                  <label for="address">Address</label>
                  <input
                    type="text"
                    formControlName="address"
                    id="Address"
                    class="form-control"
                    placeholder="Enter your Address"
                    #addressInput
                  />
                </div>
                <div class="form-group">
                  <label for="dateOfBirth">Date of Birth</label>
                  <input
                    type="date"
                    formControlName="dateofbirth"
                    id="dateOfBirth"
                    name="dateOfBirth"
                    class="form-control"
                    placeholder="Select your date of birth"
                    #dateOfBirthInput
                  />
                </div>
                <div class="form-group">
                  <label for="phone">Phone</label>
                  <input
                    type="tel"
                    formControlName="phone"
                    id="phoneNumber"
                    name="phoneNumber"
                    class="form-control"
                    placeholder="Enter your phone number"
                    #phoneNumberInput
                  />
                </div>
                <br />
                <br />
                <div class="button-container">
                  <button
                    type="submit"
                    class="btn btn-success"
                    (click)="Check()"
                  >
                    Add new customer
                  </button>
                </div>
              </form>
              <br>
              <br>
              <p
                class="text-center fs-3"
                [ngClass]="
                  isAccountCreated ? 'text-success pb-1' : 'text-danger pb-1'
                "
              >
                {{ Message }}
              </p>
            </div>
          </div>
        </div>
      </div>

      <br />
      <br />
      <br />

      <div class="table-responsive">
        <table class="table table-bordered text text-center custom-table">
          <thead>
            <tr colspan="2">
              <td><b>Customer Name</b></td>
              <td><b>Category Email</b></td>
              <td><b>Country</b></td>
              <td><b>City</b></td>
              <td><b>Address</b></td>
              <td><b>Customer Number</b></td>

              <td>
                <b> <u>Delete Customer</u></b>
              </td>
            </tr>
          </thead>
        <tbody *ngIf="customers.length > 0">
          <tr *ngFor="let customer of customers">
            <td>{{ customer.name }}</td>
            <td>{{ customer.email }}</td>
            <td>{{ customer.country }}</td>
            <td>{{ customer.city }}</td>
            <td>{{ customer.address }}</td>
            <td>{{ customer.phone }}</td>
            <td class="d-flex justify-content-center">
              <div class="px-2">
                <button class="btn fa fa-trash"
                title="Delete Customer"></button>
              </div>
            </td>
          </tr>
        </tbody>
        <tbody *ngIf="customers.length === 0">
          <tr>
            <td colspan="7" class="text-center">No users found</td>
          </tr>
        </tbody>
      </table>
    </div>
  </div>
</div>
</div>
