import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { ActivatedRoute, Router } from '@angular/router';
import { SignupService } from 'src/app/sign-up/sign-up.service';

@Component({
  selector: 'app-customer-signup',
  templateUrl: './customer-signup.component.html',
  styleUrls: ['./customer-signup.component.css'],
})
export class CustomerSignupComponent implements OnInit {
  alert = true;
  form!: FormGroup;
  Message = '';

  isAccountCreated: boolean = false;
  constructor(
    private fb: FormBuilder,
    private router: Router,
    private service: SignupService,
    private currentRoute: ActivatedRoute
  ) {
    this.form = this.fb.group({
      name: [null, [Validators.required], []],
      email: [null, [Validators.required], []],
      password: [null, [Validators.required], []],
      city: [null, [Validators.required], []],
      country: [null, [Validators.required], []],
      address: [null, [Validators.required], []],
      dateofbirth: [null, [Validators.required], []],
      phone: [null, [Validators.required], []],
    });
  }

  ngOnInit() {}
  Check(
    name: string,
    email: string,
    password: string,
    country: string,
    city: string,
    address: string,
    dateofbirth: string,
    phone: string
  ) {
    if (
      name !== '' &&
      email !== '' &&
      password !== '' &&
      country !== '' &&
      city !== '' &&
      address !== '' &&
      dateofbirth !== '' &&
      phone !== ''
    ) {
      this.alert = true;
      return true;
    }
    this.alert = false;
    return false;
  }

  public submitSignUp() {
    const formData = new FormData();
    formData.append('Name', this.form.value.name);
    formData.append('Email', this.form.value.email);
    formData.append('Password', this.form.value.password);
    formData.append('City', this.form.value.city);
    formData.append('Country', this.form.value.country);
    formData.append('Address', this.form.value.address);
    formData.append('DateOfBirth', this.form.value.dateofbirth);
    formData.append('Phone', this.form.value.phone);

    this.service.addCustomer(formData).subscribe({
      next: (res) => {
        if (res.includes('Created')) {
          this.Message = 'New user created Successfully!';
          this.isAccountCreated = true;
          this.router.navigateByUrl('/Customer-Login');
          alert('New User  Created');
        }
      },
      error: (error) => {
        if (
          error.status === 500 &&
          error.error.includes('This name already exists')
        ) {
          this.Message = 'Username Already Exists';
          this.isAccountCreated = false;
        } else if (
          error.status === 500 &&
          error.error.includes('This email already exists')
        ) {
          this.Message = 'Email already exists';
          this.isAccountCreated = false;
        } else if (
          error.status === 500 &&
          error.error.includes('This Phone already exists')
        ) {
          this.Message = 'Phone already exists';
          this.isAccountCreated = false;
        }
      },
    });
  }
}
<!DOCTYPE html>
<html lang="en">
  <head>
    <meta charset="utf-8" />
    <meta name="viewport" content="width=device-width, initial-scale=1" />
  </head>
  <body>
    <div class="container">
      <div class="row d-flex justify-content-center align-items-center vh-100">
        <form
          [formGroup]="form"
          (ngSubmit)="submitSignUp()"
          class="Signup-form"
        >
          <div class="form-group">
            <label for="Name">Name: </label>
            <input
              type="text"
              formControlName="name"
              id="name"
              class="form-control"
              placeholder="Enter your Name"
              #nameInput
            />
          </div>
          <div class="form-group">
            <label for="Email">Email: </label>
            <input
              type="email"
              formControlName="email"
              id="email"
              class="form-control"
              placeholder="Enter your Email"
              required
              #emailInput
            />
          </div>
          <div class="form-group">
            <label for="Password">Password: </label>
            <input
              type="password"
              formControlName="password"
              id="password"
              class="form-control"
              placeholder="Enter your Password"
              #passwordInput
            />
          </div>
          <div class="form-group">
            <label for="Country">Country</label>
            <input
              type="text"
              formControlName="country"
              id="Country"
              class="form-control"
              placeholder="Enter your Country"
              #countryInput
            />
          </div>
          <div class="form-group">
            <label for="City">City: </label>
            <input
              type="text"
              formControlName="city"
              id="City"
              class="form-control"
              placeholder="Enter your City"
              #cityInput
            />
          </div>
          <div class="form-group">
            <label for="Address">Address: </label>
            <input
              type="text"
              formControlName="address"
              id="Address"
              class="form-control"
              placeholder="Enter your Address"
              #addressInput
            />
          </div>
          <div class="form-group">
            <label for="dateOfBirth">Date of Birth:</label>
            <input
              type="date"
              formControlName="dateofbirth"
              id="dateOfBirth"
              name="dateOfBirth"
              class="form-control"
              placeholder="Select your date of birth"
              #dateOfBirthInput
            />
          </div>
          <div class="form-group">
            <label for="Phone">Phone: </label>
            <input
              type="tel"
              formControlName="phone"
              id="phoneNumber"
              name="phoneNumber"
              class="form-control"
              placeholder="Enter your phone number"
              #phoneNumberInput
            />
          </div>
          <div class="d-flex justify-content-center align-items-center">
            <button
              type="submit"
              class="btn w-25"
              (click)="
                Check(
                  nameInput.value,
                  emailInput.value,
                  passwordInput.value,
                  countryInput.value,
                  cityInput.value,
                  addressInput.value,
                  dateOfBirthInput.value,
                  phoneNumberInput.value
                )
              "
            >
              Sign Up
            </button>
          </div>
          <br />
          <p
            class="d-flex justify-content-center align-items-center"
            style="color: red"
            role="alert"
            *ngIf="alert === false"
          >
            Please Fill all the Fields
          </p>
          <p
            class="text-center fs-3"
            [ngClass]="
              isAccountCreated ? 'text-success pb-1' : 'text-danger pb-1'
            "
          >
            {{ Message }}
          </p>
        </form>
      </div>
    </div>
  </body>
</html>
