import { Component, ElementRef, OnInit, ViewChild } from '@angular/core';
import { headerCustomerServices } from 'src/app/header-customer/header-customer.service';
import { CategoryService } from './categories.service';
import { ToastrService } from 'ngx-toastr';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';

@Component({
  selector: 'app-categories',
  templateUrl: './categories.component.html',
  styleUrl: './categories.component.css',
})
export class CategoriesComponent implements OnInit {
  Categories: any[] = [];
  selectedFile!: File;
  name = '';
  Message = '';
  form!: FormGroup;
  isAccountCreated = false;
  categoryId!: string;
  @ViewChild('nameInput', { static: true })
  nameInput!: ElementRef<HTMLInputElement>;
  @ViewChild('myFileInput', { static: true })
  myFileInput!: ElementRef<HTMLInputElement>;
  constructor(
    private headerCustomerService: headerCustomerServices,
    private categoryService: CategoryService,
    private toastr: ToastrService,
    private fb: FormBuilder
  ) {
    this.form = fb.group({
      name: [null, [Validators.required], []],
    });
  }
  ngOnInit(): void {
    this.getAllCategories();
  }
  openEditModalCategory(category: any) {
    this.myFileInput.nativeElement.value = ''
    this.name = this.form.get('name')?.setValue(category.categoryName) ?? '';
    this.categoryId = category.categoryId;
    this.Message = '';
    this.isAccountCreated = false;
  }
  getAllCategories() {
    this.headerCustomerService.getAllCategories().subscribe({
      next: (res) => {
        this.Categories = res;
      },
      error: (error) => {
        console.log('No Categories Found');
      },
    });
  }
  onFileChange(event: Event) {
    const inputElement = event.target as HTMLInputElement;
    if (inputElement.files && inputElement.files.length > 0) {
      this.selectedFile = inputElement.files[0];
    }
  }
  Check(name: string) {
    if (name === '') {
      this.Message = 'Fill Fill The Fields';
      this.isAccountCreated = false;
    }
  }
  deleteCategory(categoryId: string) {
    this.categoryService.deleteCategory(categoryId).subscribe({
      next: (res) => {
        console.log(res);
        if (res.includes('Category Deleted Succefully')) {
          const findIndex = this.Categories.findIndex(
            (item) => item.categoryId === categoryId
          );
          if (findIndex !== -1) {
            this.Categories.splice(findIndex, 1);
            this.toastr.success('Deleted Successfully', 'Success');
          }
        }
      },
      error: (error) => {
        this.toastr.error("Couldn't Delete The Category", 'Failed');
      },
    });
  }
  editCategoryForm() {
    const formData = new FormData();
    formData.append('categoryId', this.categoryId);
    formData.append('categoryName', this.form.value.name);
    formData.append('categoryImage', this.selectedFile);
    this.categoryService.modifyCategory(formData).subscribe({
      next: (res) => {
        if (res.includes('Category Updated Succefully')) {
          const categoryIndex = this.Categories.findIndex(
            (item) => item.categoryId === this.categoryId
          );
          if (categoryIndex !== -1) {
            this.Message = 'Category Updated Succefully';
            this.isAccountCreated = true;
            this.Categories[categoryIndex].categoryName = this.form.value.name;
          }
        }
      },
      error: (error) => {
        this.Message = error.error;
        this.isAccountCreated = false;
      },
    });
  }
  updateCategoryList(Data: any[]) {
    this.Categories.push(Data);
  }
}
<app-header></app-header>
<div class="container-fluid">
  <div class="row">
    <div class="col-md-12">
      <h3 class="text text-center">These Are Our Categories!</h3>
      <br />
      <app-add-category (categoryListChanged)="updateCategoryList($event)"></app-add-category>
      <div class="table-responsive">
        <table class="table table-bordered text text-center custom-table">
          <thead>
            <tr colspan="2">
              <td><b>Category ID</b></td>
              <td><b>Category Name</b></td>
            
              <td>
                <b> <u>Modify Category</u></b>
              </td>
            </tr>
          </thead>
        <tbody *ngIf="Categories.length > 0">
          <tr *ngFor="let category of Categories">
            <td>{{ category.categoryId }}</td>
            <td>{{ category.categoryName }}</td>
            <td class="d-flex justify-content-center">
              <div class="px-2">
                <button
                class="btn fa fa-edit"
                title="Modify Category" 
                (click)="openEditModalCategory(category)"
                data-bs-toggle="modal"
                data-bs-target="#EditCategory"
                ></button>
              </div>
              <div class="px-2">
                <button class="btn fa fa-trash"
                title="Delete Category" 
                (click)="deleteCategory(category.categoryId)"
                ></button>
              </div>
            </td>
          </tr>
        </tbody>
        <tbody *ngIf="Categories.length === 0">
          <tr>
            <td colspan="3" class="text-center">No Categories found</td>
          </tr>
        </tbody>
      </table>
    </div>
  </div>
</div>
</div>
<div
  class="modal"
  id="EditCategory"
  tabindex="-1"
  role="dialog"
  aria-labelledby="EditCategoryLabel"
  aria-hidden="true"
>
  <div class="modal-dialog">
    <div class="modal-content">
      <div class="modal-header">
        <button
          type="button"
          class="close p-2 mt-1"
          data-bs-dismiss="modal"
          aria-label="Close"
        >
          <span aria-hidden="true">&times;</span>
        </button>
        <h4 class="modal-title" id="EditCategoryLabel" >Edit Category</h4>
      </div>
      <div class="modal-body">
        <form [formGroup]="form" (ngSubmit)="editCategoryForm()" >
          <div class="form-group">
            <label for="name">Name</label>
            <input
              type="text"
              formControlName="name"
              id="name"
              class="form-control"
              value="{{name}}"
              placeholder="Please enter a Product Name"
              #nameInput
            />
          </div>
          
          <div class="form-group">
            <label for="photo">Photo</label>
            <input
              type="file"
              (change)="onFileChange($event)"
              name="photo"
              id="photo"
              class="form-control"
              accept="image/*"
              #myFileInput
              
            />
          </div>

          <br />
          <br />
          <div class="button-container d-flex justify-content-end">
            <button
              type="submit"
              class="btn btn-success"
              (click)="
                Check(
                  nameInput.value
                )
              "
            >
              Edit Category
            </button>
          </div>
        </form>
        <br>
        <br>
        <p
          class="text-center fs-3"
          [ngClass]="
            isAccountCreated ? 'text-success pb-1' : 'text-danger pb-1'
          "
        >
          {{ Message }}
        </p>
      </div>
    </div>
  </div>
</div>
